if ( !class_exists( 'Kirki' ) ) {
	return;
}


Kirki::add_panel( 'colors', array(
	'priority'	 => 10,
	'title'		 => esc_attr__( 'Colors and Typography', 'futurio-extra' ),
) );

Kirki::add_section( 'presets_colors_section', array(
	'title'		 => esc_attr__( 'Color presets', 'futurio-extra' ),
	'panel'		 => 'colors',
	'priority'	 => 5,
) );


/**
 * Presets
 */
